<template>
	<Container style="padding-top: 100rpx;" v-model="current" tabbar :is-back="false"
	 :style="[{backgroundColor:'var(--background-color-1)'}]">
		<template v-if="current==='home'">
			<view slot="action" v-if="userType+''!=='1'&&pickerData.length>0">
				<picker @change="PickerChange" :value="index" :range="pickerData">
					<view class="picker">
						<text class="text" style="padding-right:16rpx">
							{{index>-1?pickerData[index]:'选择客户'}}
						</text>
						<Arrow style-inner="border-color:#fff;border-width: 4rpx 4rpx 0 0;" :size="20" />
					</view>
				</picker>
			</view>
			<view class="cu-bar search fixed" :style="[{top:CustomBar + 'px',backgroundColor:'var(--background-color-0)'}]">
				<view class="search-form round">
					<text class="cuIcon-search"style="color:var(--font-color-1)"></text>
					<input type="text"  @blur="nameChange" placeholder="请输入药材名称/拼音缩写" confirm-type="search" style="background-color:transparent;color:var(--font-color-0)" />
				</view>
				<view class="action">
					<button @click="search" class="cu-btn shadow-blur round" style="background-color:var(--background-color-main-0);color:#fff;">搜索</button>
				</view>
			</view>
			<scroll-view scroll-y class="indexes" :scroll-into-view="'indexes-'+ listCurID" :style="[{height:'calc(100vh - '+ CustomBar + 'px - 50px)',backgroundColor:'var(--background-color-1)'}]"
			 :scroll-with-animation="true" :enable-back-to-top="true">
				<block v-for="(item,index) in list" :key="index">
					<view :class="'indexItem-' + item.name" :id="'indexes-' + item.name" :data-index="item.name">
						<view class="padding">{{item.name}}</view>
						<view class="cu-list menu-avatar no-padding">
							<view class="cu-item" v-for="(items,sub) in item.data" :key="sub">
								<view class="cu-avatar round lg">{{items.name?items.name.charAt(0):''}}</view>
								<view class="content">
									<view class="content-left"><text>{{items.name}}</text>
										<text>{{items.equivalent}}/{{items.specifications}}</text>
										<text>￥{{items.price}}</text>
									</view>
									<view :class="[items.active?'active':'', 'img']"> 
									  <image  @click="addShoppingCart(items,index,sub)" src="data:image/png;base64," />
									</view>
								</view>
							</view>
						</view>
					</view>
				</block>
			</scroll-view>
			<view class="index-bar" :style="[{height:'calc(100vh - ' + CustomBar + 'px - 50px)'}]">
				<view class="index-bar-box" @touchstart="tStart" @touchend="tEnd" @touchmove.stop="tMove">
					<view class="index-bar-item" v-for="(item,index) in list3" :key="index" :id="index" @touchstart="getCur" @touchend="setCur">
						{{item.name}}</view>
				</view>
			</view>
			<!--选择显示-->
			<view v-show="!hidden" class="index-toast">
				{{listCur}}
			</view>
		</template>
	
		<Mine v-else-if="current==='mine'" />
		<ShoppingCart v-else-if="current==='shopping-cart'" />
		<Order v-else-if="current==='order'||current==='track'" :mode="current"></Order>
	</Container>
</template>

<script>
	import Arrow from '@/components/arrow';
	import Mine from '../mine';
	import Order from '../order';
	import ShoppingCart from '../shopping-cart';
	import {
		getMini,
		cart,
		getCartPage,
		getPharmacyList
	} from '@/api/auth';
	export default {
		components: {
			Arrow,
			Mine,
			ShoppingCart,
			Order
		},
		onPullDownRefresh() {
			uni.stopPullDownRefresh();
		},
		data() {
			return {
				StatusBar: this.StatusBar,
				CustomBar: this.CustomBar,
				current: 'home',
				hidden: true,
				listCurID: '',
				list: [],
				list2: [],
				list3: [],
				listCur: '',
				index: 0,
				picker: [],
				shoppingCartCount: 0,
				loding: true,
				time: null,
				searchContent: null,
				count: 0,
				active: null
			};
		},
		computed: {
			userType(){
			  return this.$store.getLoginInfo()?.userType||'1'
			},
			pickerData(){
				return this.picker.map(item=>item.name)
			}
		},
		watch:{
			current(val){
				this.list=[];
				clearInterval(this.time);
				this.time=null;
				this.count=0;
				if(val==='home') {
					this.list= this.list2.slice(this.count,this.count+3);
					this.count=this.count+3;
					this.handleList();
				}
			}
		},
		onShow(){
			uni.$emit('show',true);
			this.getCartPage()
		},
		async onLoad() {
			let that = this;
			this.$loading.open()
			await	this.getMini();
			this.getCartPage();
			this.getPharmacyList();
			uni.$on('cleanUpCache',()=>{
				this.getMini();
			})
		},
		async onReady() {
			let that = this;
			uni
				.createSelectorQuery()
				.select('.index-bar-box')
				.boundingClientRect(function(res) {
					that.boxTop = res.top;
				})
				.exec();
			uni
				.createSelectorQuery()
				.select('.indexes')
				.boundingClientRect(function(res) {
					that.barTop = res.top;
				})
				.exec();
		},
		methods: {
			getPharmacyList(){
				let info = this.$store.getLoginInfo();
				if(!(info&&info.userId)) return;
				getPharmacyList(info.userId).then(({data})=>{
					this.picker=data?.data;
					this.$store.setPharmacy(this.picker[0])
				})
			},
			search(){
				const that=this;
				that.list=that.list2.filter(item=>{
					return item.name===that.searchContent||item.data.some(item2=>{
						return item2.name.indexOf(that.searchContent)!==-1
					})
				})
			},
			nameChange(e){
				this.searchContent=e.detail.value;
			},
			handleList(){
				const that=this;
				that.time=setInterval(()=>{
					if(that.list.length>=that.list2.length) clearInterval(that.time);
					that.list.push(...that.list2.slice(that.count,that.count+3))
					that.count=that.count+3;
				},1000);
			},
			async getMini() {
				const that = this;
				getMini().then(({
					data
				}) => {
					let list = [];
					let list2 = [];
					let i = 0;
					let obj = data?.data || {};
					for (let [key, value, b] of Object.entries(obj)) {
						list[i] = {
							name: key,
							data: value
						};
						list2[i] = {
							name: key,
							data: i<2?value:[]
						};
						i++;
					}
					that.list2= list;
					that.list3= list2;
					that.list= list.slice(that.count,that.count+3);
					that.count=that.count+3;
				  that.handleList();
					that.listCur = that.list[0];
					that.$loading.close()
				}).finally((e)=>{
					that.$loading.close()
				})
			},
			getCartPage() {
				const that = this;
				let data = that.$store.getLoginInfo();
				if(!(data&&data.userId)) return;
				getCartPage({
					userType: data.userType,
					userId: data.userId,
					pageNo: 1,
					pageSize: 10,
				}).then((res) => {
					if(res.resCode===0){
						const data=res.data;
						const list = data?.data?.list?.cartDetails|| [];
						that.shoppingCartCount=list.length
						uni.$emit('shoppingCartCount',that.shoppingCartCount);
						that.$loading.close()
					}
				}).finally((res) => {
					that.$loading.close()
				})
			},
			addShoppingCart(val,index,sub) {
				const that = this;
				let data = that.$store.getLoginInfo();
				if(!(data&&data.userId)){
					that.$tips('提示','您没有登录');
					uni.navigateTo({
						url: '/pages/login/index',
					});
					return;
				};
				cart({
					"userId": data.userId,
					"userType": data.userType,
					"medicineId": val.id,
					"medicineName": val.name,
					"price": val.price,
					"specifications": val.specifications,
					"equivalent": val.equivalent,
					"number": 1
				}).then((res)=>{
					if(res.resCode===0){
						that.$tips('成功',res.resMsg);
						const obj=this.list[index].data[sub];
						this.$set(this.list[index].data,sub,{
							...obj,
							active: true
						});
						that.shoppingCartCount++;
						uni.$emit('shoppingCartCount',that.shoppingCartCount);
					}else{
						that.$tips('失败',res.resMsg);
					}
				})
			},
			PickerChange(e) {
				this.index = e.detail.value;
				this.$store.setPharmacy(this.picker[this.index])
			},
			//获取文字信息
			getCur(e) {
				this.hidden = false;
				this.listCur = this.list[e.target.id].name;
			},
			setCur(e) {
				this.hidden = true;
				this.listCur = this.listCur;
			},
			//滑动选择Item
			tMove(e) {
				let y = e.touches[0].clientY,
					offsettop = this.boxTop,
					that = this;
				//判断选择区域,只有在选择区才会生效
				if (y > offsettop) {
					let num = parseInt((y - offsettop) / 20);
					this.listCur = that.list[num].name;
				}
			},

			//触发全部开始选择
			tStart() {
				this.hidden = false;
			},

			//触发结束选择
			tEnd() {
				this.hidden = true;
				this.listCurID = this.listCur;
			},
			indexSelect(e) {
				let that = this;
				let barHeight = this.barHeight;
				let list = this.list;
				let scrollY = Math.ceil((list.length * e.detail.y) / barHeight);
				for (let i = 0; i < list.length; i++) {
					if (scrollY < i + 1) {
						that.listCur = list[i].name;
						that.movableY = i * 20;
						return false;
					}
				}
			},
		},
		destroyed() {
			clearInterval(this.time)
		}
	};
</script>

<style lang="scss">
	.content {
		display: flex;
		flex-direction: row;
		align-items: center;

		image {
			width: 40rpx;
			height: 40rpx;
		}
	}

	.content-left {
		flex: 1;

		text {
			flex: 1;
		}
	}

	.picker {
		font-size: 36rpx;
		padding: 0 28rpx;
		display: flex;
		flex-direction: row;
		align-items: center;

		.text {
			overflow: hidden;
			white-space: nowrap;
			color: #fff;
		}
	}
	.menu-avatar .img{
		position: relative;
	}
	.menu-avatar .img.active::after{
		position: absolute;
		content: "";
		right: 0;
		top: 0;
		width: 6px;
		height: 6px;
		border-radius: 50%;
		overflow: hidden;
		background-color: #dd514c;
	}
	
	.indexes {
		position: relative;
		padding-bottom: calc(env(safe-area-inset-bottom) / 2 + 100rpx);
	}

	.index-bar {
		position: fixed;
		right: 0px;
		bottom: 0px;
		padding: 20upx 20upx 20upx 60upx;
		display: flex;
		align-items: center;
	}

	.index-bar .index-bar-box {
		width: 40upx;
		height: auto;
		background: var(--background-color-0);
		display: flex;
		flex-direction: column;
		box-shadow: var(--box-shadow-0);
		border-radius: 10upx;
	}

	.index-bar-item {
		flex: 1;
		width: 40upx;
		height: 40upx;
		display: flex;
		align-items: center;
		justify-content: center;
		font-size: 24upx;
	}

	movable-view.index-bar-item {
		width: 40upx;
		height: 40upx;
		z-index: 9;
		position: relative;
	}

	movable-view.index-bar-item::before {
		content: '';
		display: block;
		position: absolute;
		left: 0;
		top: 10upx;
		height: 20upx;
		width: 4upx;
		background-color: #f37b1d;
	}

	.index-toast {
		position: fixed;
		top: 0;
		right: 80upx;
		bottom: 0;
		background: rgba(0, 0, 0, 0.5);
		width: 100upx;
		height: 100upx;
		border-radius: 10upx;
		margin: auto;
		color: #fff;
		line-height: 100upx;
		text-align: center;
		font-size: 48upx;
	}
</style>
